        public static uint Ror(uint val, int r_bits, int max_bits)
        {
            return (val >> r_bits) | (val << (max_bits - r_bits));
        }

        public static uint HashFunction(string name)
        {
            uint functionHash = 0;

            name += "\x00";

            foreach (char c in name)
            {
                functionHash = Ror(functionHash, 13, 32);
                functionHash += c;
            }

            return functionHash;
        }

